import { Component } from '@angular/core';

@Component({
  selector: 'app-task5',
  templateUrl: './task5.component.html',
  styleUrls: ['./task5.component.css']
})
export class Task5Component {
  name: string = 'Іван';

  changeName(): void {
    this.name = 'Дмитро';
  }
}
<p>{{ name }}</p>
<a (click)="changeName()">Змінити ім'я</a>
