import { Component } from '@angular/core';

@Component({
  selector: 'app-task6',
  templateUrl: './task6.component.html',
  styleUrls: ['./task6.component.css']
})
export class Task6Component {
  name: string = 'Іван';
  surname: string = 'Іванів';
  age: number = 25;

  changeName(): void {
    this.name = 'Петро';
  }

  changeSurname(): void {
    this.surname = 'Петренко';
  }

  changeAge(): void {
    this.age = 30;
  }
}
<h2>Змінні:</h2>
<p>Ім'я: {{ name }}</p>
<p>Прізвище: {{ surname }}</p>
<p>Вік: {{ age }}</p>

<a (click)="changeName()">Змінити ім'я</a>
<a (click)="changeSurname()">Змінити прізвище</a>
<a (click)="changeAge()">Змінити вік</a>
